void mainViewBase::buttonCallbackHandler(const touchgfx::AbstractButton& src)
{
    if (&src == &reverseCamButton)
    {
        //Interaction1
        //When reverseCamButton clicked change screen to map
        //Go to map with no screen transition
        application().gotomapScreenNoTransition();
        //Interaction2
        //When reverseCamButton clicked change screen to graph
        //Go to graph with no screen transition
        application().gotographScreenNoTransition();
        //Interaction3
        //When reverseCamButton clicked change screen to camera
        //Go to camera with no screen transition
        application().gotocameraScreenNoTransition();
        //Interaction4
        //When reverseCamButton clicked change screen to conf
        //Go to conf with no screen transition
        application().gotoconfScreenNoTransition();
        //Interaction5
        //When reverseCamButton clicked change screen to Settings
        //Go to Settings with no screen transition
        application().gotoSettingsScreenNoTransition();
        //Interaction6
        //When reverseCamButton clicked change screen to notifications
        //Go to notifications with no screen transition
        application().gotonotificationsScreenNoTransition();
    }
}
